import React, { useRef, useState } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Algebra1 from "../assets/formula1.png";
import Algebra2 from "../assets/formula3.png";
import Algebra3 from "../assets/formula4.png";

const mathscontent = [
  {
    formula: Algebra1,
    topic: "Arithmetic",
    description: "Introductory",
  },
  {
    formula: "3x + 5 = 4",
    topic: "Basic Algebra",
    description: "Foundational",
  },
  {
    formula: Algebra2,
    topic: "Intermediate Algebra",
    description: "Intermediate",
  },
  {
    formula: Algebra3,
    topic: "Calculus",
    description: "Advanced",
  },
];

export default function Mathcomfort({ onPageChange }) {
  const [selectedCard, setSelectedCard] = useState(null);
  const [continueEnabled, setContinueEnabled] = useState(false);

  const cardRefs = useRef([]);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setContinueEnabled(true);
  };

  const handleMouseMove = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "#3f51b5"; // Change border color on hover
    }
  };

  const handleMouseLeave = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "rgba(0, 0, 0, 0.23)"; // Reset border color on mouse leave
    }
  };

  const handleContinue = () => {
    if (selectedCard !== null) {
      onPageChange(); // Call the callback function provided by SurveyComponent
    }
  };

  return (
    <>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          width: "80%", // Set width to 80%
          margin: "auto", // Center the container
        }}
      >
        <div style={{ width: "100%" }}>
          <p
            style={{
              fontFamily: "Roboto",
              fontSize: 22,
              fontWeight: "bold",
              textAlign: "center",
            }}
          >
            What is your math comfort level?
          </p>
          <p
            style={{
              fontFamily: "Roboto",
              textAlign: "center",
              marginBottom: "20px",
            }}
          >
            Choose the highest level you feel confident in - you can always
            adjust later.
          </p>
        </div>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap", // Allow cards to wrap onto the next line if needed
            justifyContent: "center", // Align cards horizontally at the center
          }}
        >
          {mathscontent.map((math, index) => (
            <Card
              key={index}
              ref={(ref) => (cardRefs.current[index] = ref)}
              variant="outlined"
              style={{
                height: "200px",
                width: "200px", // Make each card take full width initially
                marginBottom: 10,
                marginRight: 20,
                transition: "border-color 0.5s", 
                cursor: "pointer", 
                borderColor:
                  selectedCard === index ? "#4caf50" : "rgba(0, 0, 0, 0.23)", // Apply selected card style
                boxShadow:
                  selectedCard === index
                    ? "0px 3px 6px rgba(0, 0, 0, 0.16)"
                    : "none", // Apply shadow for selected card
              }}
              onClick={() => handleCardClick(index)}
              onMouseMove={() => handleMouseMove(index)}
              onMouseLeave={() => handleMouseLeave(index)}
            >
              <CardContent
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                <img
                  src={math.formula}
                  alt="Chart"
                  style={{
                    marginBottom: "10px",
                    width: "120px",
                    height: "65px",
                  }}
                />
                <Typography component="div">{math.topic}</Typography>
                <Typography component="div">{math.description}</Typography>
              </CardContent>
            </Card>
          ))}
        </div>
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          {continueEnabled && (
            <button
              onClick={handleContinue}
              style={{
                padding: "8px 26px",
                backgroundColor: "black",
                color: "white",
                border: "1px solid #ccc",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Continue
            </button>
          )}
        </div>
      </div>
    </>
  );
}
