import React, { useRef, useState,useEffect } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Globe from "../assets/globe.png";
import Chart from "../assets/chart.png";
import Math from "../assets/math.png";
import Target from "../assets/target.png";
import { MathJax, MathJaxContext } from "react-mathjax3";

import Eyes from "../assets/eyes.png";
const mathscontent = [
  {
    formula: "5 × \frac{1|2} = ?",
    topic: "Arithmetic",
    description: "Introductory",
  },
  {
    formula: "3x + 5 = 4",
    topic: "Basic Algebra",
    description: "Foundational",
  },
  {
    formula: "x = \frac{-b±sqrt{b^2 - 4ac}}{2a}",
    topic: "Intermediate Algebra",
    description: "Intermediate",
  },
  {
    formula: "int_{0}^{L} x^2 dx = ?",
    topic: "Calculus",
    description: "Advanced",
  },
];

const Intrestpage = ({ onPageChange }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const [continueEnabled, setContinueEnabled] = useState(false);

  const cardRefs = useRef([]);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setContinueEnabled(true);
  };

  const handleMouseMove = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "#3f51b5"; // Change border color on hover
    }
  };

  const handleMouseLeave = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "rgba(0, 0, 0, 0.23)"; // Reset border color on mouse leave
    }
  };

  const handleContinue = () => {
    if (selectedCard !== null) {
      onPageChange(); // Call the callback function provided by SurveyComponent
    }
  };
  useEffect(() => {
    // Trigger MathJax typesetting whenever the component updates
    MathJax.typeset();
  }, []);
  return (
    <>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          width: "80%", // Set width to 80%
          margin: "auto", // Center the container
        }}
      >
        <div style={{ width: "100%" }}>
          <p
            style={{
              fontFamily: "Roboto",
              fontSize: 22,
              fontWeight: "bold",
              textAlign: "center",
            }}
          >
            Which are you most Intrested in?
          </p>
          <p
            style={{
              fontFamily: "Roboto",
              textAlign: "center",
              marginBottom: "20px",
            }}
          >
            Choose just one. This will help us get you started (but won't limit
            your experience).
          </p>
        </div>
        <div>
          {mathscontent.map((item, index) => (
            <div key={index} style={{ marginBottom: "20px", width: "100%" }}>
              <h2>{item.formula}</h2>
              <p>Topic: {item.topic}</p>
              <p>Description: {item.description}</p>
            </div>
          ))}
        </div>
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          {continueEnabled && (
            <button
              onClick={handleContinue}
              style={{
                padding: "8px 26px",
                backgroundColor: "black",
                color: "white",
                border: "1px solid #ccc",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Continue
            </button>
          )}
        </div>
      </div>
    </>
  );
};

export default Intrestpage;
