import React from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import ChartImage from "../assets/chart.png";

export default function Intrestpage({ currentPage, totalPages, handleContinue }) {
  const CardContents = [
    {
      image: ChartImage,
      description: "Student or soon to be enrolled",
    },
    {
      image: ChartImage,
      description: "Professional pursuing a career",
    },
    {
      image: ChartImage,
      description: "Parent of a school-age-child",
    },
    {
      image: ChartImage,
      description: "Lifelong learner",
    },
    {
      image: ChartImage,
      description: "Teacher",
    },
    {
      image: ChartImage,
      description: "Other",
    },
  ];

  const handleCardClick = () => {
    // Handle card selection logic here
    console.log("Card clicked");
  };

  return (
    <div>
      <div style={{ width: "100%" }}>
        <p style={{ fontFamily: "Roboto", fontSize: 22, fontWeight: "bold", textAlign: "center" }}>
          Which describes you best?
        </p>
        <p style={{ fontFamily: "Roboto", textAlign: "center", marginBottom: "20px" }}>
          This will help us personalize your experience.
        </p>
      </div>
      <div>
        {CardContents.map((content, index) => (
          <Card
            key={index}
            variant="outlined"
            style={{
              height: "70px",
              width: "100%", // Make each card take full width initially
              marginBottom: 10,
              marginLeft: 1, // Add some margin between cards
              marginRight: 20,
              cursor: "pointer", // Change cursor on hover
            }}
            onClick={handleCardClick}
          >
            <CardContent style={{ display: "flex", alignItems: "center" }}>
              <img src={content.image} alt="Chart" style={{ marginRight: "10px", width: "50px", height: "40px" }} />
              <div>
                <Typography component="div">{content.description}</Typography>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
      <div style={{ textAlign: "center", marginTop: "20px" }}>
        {currentPage < totalPages && (
          <button
            onClick={handleContinue}
            style={{
              padding: "8px 26px",
              backgroundColor: "black",
              color: "white",
              border: "1px solid #ccc",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Continue
          </button>
        )}
      </div>
    </div>
  );
}
