import React from "react";
import Box from "@mui/material/Box";
import LinearProgress from "@mui/material/LinearProgress";
import { createTheme } from '@mui/material/styles';

const Progressbar = ({ currentPage, totalPages }) => {
  const progress = (currentPage / totalPages) * 100;
  const theme = createTheme({
    palette: {
      ochre: {
        main: '#E3D026',
        light: '#E9DB5D',
        dark: '#A29415',
        contrastText: '#242105',
      },
    },
  });
  const customStyles = {
    backgroundColor: "#e5e7eb",
    height: "5px",
    borderRadius: "10px",
    marginTop: "20px",
    marginBottom: "20px",
    marginLeft: "10%",
    marginRight: "10%"
  };

  const color = "#15a387"; // Color of the progress bar

  return (
    <Box sx={{ width: "80%", marginLeft: "auto", marginRight: "auto" }}>
      <LinearProgress
        variant="determinate"
        value={progress}
        color="primary" // Change the color here
        sx={{ ...customStyles, theme }}
      />
    </Box>
  );
};

export default Progressbar;
