import React, { useRef, useState } from "react";
import Student from "../assets/student.png";
import Professional from "../assets/professional.png";
import Parent from "../assets/parent.png";
import Lifelong from "../assets/lifelong.png";
import Teacher from "../assets/teacher.png";
import Other from "../assets/other.png";

const CardContents = [
  {
    image: Student,
    description: "Student or soon to be enrolled",
  },
  {
    image: Professional,
    description: "Professional pursuing a career",
  },
  {
    image: Parent,
    description: "Parent of a school-age-child",
  },
  {
    image: Lifelong,
    description: "Lifelong learner",
  },
  {
    image: Teacher,
    description: "Teacher",
  },
  {
    image: Other,
    description: "Other",
  },
];

const Describe = ({ onPageChange }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const [continueEnabled, setContinueEnabled] = useState(false);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setContinueEnabled(true);
  };

  const handleContinue = () => {
    if (selectedCard !== null) {
      onPageChange();
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <div className="w-80">
        <h1 className="font-bold text-3xl mb-6 text-center">
          Which describes you best?
        </h1>
        <p className="text-base mb-6 text-center">
          This will help us personalize your experience.
        </p>
      </div>
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
        {CardContents.map((content, index) => (
          <div
            key={index}
            className={`border border-gray-300 rounded-lg overflow-hidden ${
              selectedCard === index ? "border-green-500 shadow-md" : ""
            }`}
            onClick={() => handleCardClick(index)}
            style={{ cursor: "pointer" }}
          >
            <div className="p-4 flex items-center">
              <img
                src={content.image}
                alt="Card"
                className="w-12 h-12 mr-4"
              />
              <p className="text-sm">{content.description}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="mt-8">
        {continueEnabled && (
          <button
            onClick={handleContinue}
            className="px-6 py-2 bg-black text-white rounded-md border border-gray-400 hover:bg-gray-800 hover:border-gray-800 focus:outline-none focus:ring focus:ring-gray-300"
          >
            Continue
          </button>
        )}
      </div>
    </div>
  );
};

export default Describe;
