import React, { useRef, useState } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import LinearProgress from "@mui/material/LinearProgress";
import Student from "../assets/student.png";
import Professional from "../assets/professional.png";
import Parent from "../assets/parent.png";
import Lifelong from "../assets/lifelong.png";
import Teacher from "../assets/teacher.png";
import Other from "../assets/other.png";

export default function Intrestpage() {
  const CardContents = [
    {
      image: Student,
      description: "Student or soon to be enrolled",
    },
    {
      image: Professional,
      description: "Professional pursuing a career",
    },
    {
      image: Parent,
      description: "Parent of a school-age-child",
    },
    {
      image: Lifelong,
      description: "Lifelong learner",
    },
    {
      image: Teacher,
      description: "Teacher",
    },
    {
      image: Other,
      description: "Other",
    },
  ];

  const [selectedCard, setSelectedCard] = useState(null);
  const [continueEnabled, setContinueEnabled] = useState(false);
  const [progress, setProgress] = useState(0);

  const cardRefs = useRef([]);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setContinueEnabled(true);
    setProgress((index + 1) * (100 / CardContents.length));
  };

  const handleMouseMove = (index) => {
    if (selectedCard === index) {
      return;
    }
    cardRefs.current[index].style.borderColor = "#3f51b5"; // Change border color on hover
  };

  const handleMouseLeave = (index) => {
    if (selectedCard === index) {
      return;
    }
    cardRefs.current[index].style.borderColor = "rgba(0, 0, 0, 0.23)"; // Reset border color on mouse leave
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "80%", // Set width to 80%
        margin: "auto", // Center the container
      }}
    >
      <div style={{ width: "100%" }}>
        <p
          style={{
            fontFamily: "Roboto",
            fontSize: 22,
            fontWeight: "bold",
            textAlign: "center",
          }}
        >
          Which describes you best?
        </p>
        <p
          style={{
            fontFamily: "Roboto",
            textAlign: "center",
            marginBottom: "20px",
          }}
        >
          This will help us personalize your experience.
        </p>
      </div>
      <div>
        {CardContents.map((content, index) => (
          <Card
            key={index}
            ref={(ref) => (cardRefs.current[index] = ref)}
            variant="outlined"
            style={{
              height: "70px",
              width: "100%", // Make each card take full width initially
              marginBottom: 10,
              marginLeft: 1, // Add some margin between cards
              marginRight: 20,
              transition: "border-color 0.5s", // Smooth transition for border color change
              cursor: "pointer", // Change cursor on hover
              borderColor: selectedCard === index ? "#4caf50" : "rgba(0, 0, 0, 0.23)", // Apply selected card style
              boxShadow:
                selectedCard === index ? "0px 3px 6px rgba(0, 0, 0, 0.16)" : "none", // Apply shadow for selected card
            }}
            onClick={() => handleCardClick(index)}
            onMouseMove={() => handleMouseMove(index)}
            onMouseLeave={() => handleMouseLeave(index)}
          >
            <CardContent style={{ display: "flex", alignItems: "center" }}>
              <img
                src={content.image}
                alt="Chart"
                style={{ marginRight: "10px", width: "50px", height: "40px" }}
              />
              <div>
                <Typography component="div">{content.description}</Typography>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
      
    </div>
  );
}
