import React, { useRef, useState } from "react";
import Student from "../assets/student.png";
import Professional from "../assets/professional.png";
import Parent from "../assets/parent.png";
import Lifelong from "../assets/lifelong.png";
import Teacher from "../assets/teacher.png";
import Other from "../assets/other.png";

const CardContents = [
  {
    image: Student,
    description: "Student or soon to be enrolled",
  },
  {
    image: Professional,
    description: "Professional pursuing a career",
  },
  {
    image: Parent,
    description: "Parent of a school-age-child",
  },
  {
    image: Lifelong,
    description: "Lifelong learner",
  },
  {
    image: Teacher,
    description: "Teacher",
  },
  {
    image: Other,
    description: "Other",
  },
];

const Describe = ({ onPageChange }) => {
  const [selectedCard, setSelectedCard] = useState(null);
  const [continueEnabled, setContinueEnabled] = useState(false);

  const cardRefs = useRef([]);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setContinueEnabled(true);
  };

  const handleMouseMove = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "#3f51b5"; 
    }
  };

  const handleMouseLeave = (index) => {
    if (selectedCard !== index) {
      cardRefs.current[index].style.borderColor = "rgba(0, 0, 0, 0.23)"; 
    }
  };

  const handleContinue = () => {
    if (selectedCard !== null) {
      onPageChange(); 
    }
  };

  return (
    <>
      <div className="flex flex-col items-center w-80 mx-auto">
        <div className="w-full">
          <p className="font-bold text-xl md:text-3xl lg:text-4xl my-4 text-center">
            Which describes you best?
          </p>
          <p className="text-center mb-4">
            This will help us personalize your experience.
          </p>
        </div>
        <div>
          {CardContents.map((content, index) => (
            <div
              key={index}
              ref={(ref) => (cardRefs.current[index] = ref)}
              className={`border-2 border-opacity-0 hover:border-green-500 ${
                selectedCard === index
                  ? "border-green-500 shadow-md"
                  : "border-opacity-0"
              } transition duration-500 cursor-pointer`}
              style={{ height: "70px" }}
              onClick={() => handleCardClick(index)}
              onMouseMove={() => handleMouseMove(index)}
              onMouseLeave={() => handleMouseLeave(index)}
            >
              <div className="flex items-center justify-center">
                <img
                  src={content.image}
                  alt="Chart"
                  className="mr-2 w-10 h-9"
                />
                <div>
                  <p className="text-sm md:text-base">{content.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="text-center mt-4">
          {continueEnabled && (
            <button
              onClick={handleContinue}
              className="py-2 px-6 bg-black text-white border border-gray-400 rounded cursor-pointer"
            >
              Continue
            </button>
          )}
        </div>
      </div>
    </>
  );
};

export default Describe;
